import Head from 'next/head'

export default function Home() {
  return (
    <div className="container">
      <Head>
        <title>Sahil Lavingia</title>
        <link rel="icon" href="/favicon.svg" />
      </Head>

      <main>
        <h1 className="title">
          Sahil Lavingia
        </h1>

        <a href="https://twitter.com/shl">
          <img src="/twitter.png" className="twitter" />
        </a>

        <a href="mailto:sahil@hey.com">
          <img src="/email.svg" className="email" />
        </a>

        <label>
          Founder <span className="amp">&amp;</span> CEO
        </label>

        <h2 className="item gumroad">
          <a href="https://gumroad.com">
            Gumroad
          </a>
        </h2>

        <label>
          Writer
        </label>

        <h2 className="item">
          Reflecting on my Failure to Build a Billion-Dollar Company
        </h2>

        <h2 className="item">
          From Bubble to Bubble
        </h2>

        <label>
          Angel investor in startups including
        </label>

        <h2 className="item">
        HelloSign, Movable Ink, Lambda School, Squad, Haus, Figma, and Clubhouse.
        </h2>
      </main>

      <hr />

      <form action="https://gumroad.com/follow_from_embed_form" className="form gumroad-follow-form-embed" method="post">
        <input name="seller_id" type="hidden" value="2614450981218" />
        <input name="email" placeholder="Your email address" type="email" />
        <button data-custom-highlight-color="" type="submit">Subscribe to random creative musings</button>
      </form>

      <style jsx>{`
        .gumroad-follow-form-embed { zoom: 1; } .gumroad-follow-form-embed:before, .gumroad-follow-form-embed:after { display: table; line-height: 0; content: ""; } .gumroad-follow-form-embed:after { clear: both; } .gumroad-follow-form-embed * { margin: 0; border: 0; padding: 0; outline: 0; box-sizing: border-box !important; float: left !important; } .gumroad-follow-form-embed input { border-radius: 4px; border-top-right-radius: 0; border-bottom-right-radius: 0; font-family: -apple-system, ".SFNSDisplay-Regular", "Helvetica Neue", Helvetica, Arial, sans-serif; font-size: 15px; line-height: 20px; background: #fff; border: 1px solid #ddd; border-right: 0; color: #aaa; padding: 10px; box-shadow: inset 0 1px 0 rgba(0, 0, 0, 0.02); background-position: top right; background-repeat: no-repeat; text-rendering: optimizeLegibility; font-smoothing: antialiased; -webkit-appearance: none; -moz-appearance: caret; width: 50% !important; height: 40px !important; } .gumroad-follow-form-embed button { border-radius: 4px; border-top-left-radius: 0; border-bottom-left-radius: 0; box-shadow: 0 1px 1px rgba(0, 0, 0, 0.12); -webkit-transition: all .05s ease-in-out; transition: all .05s ease-in-out; display: inline-block; padding: 11px 15px 12px; cursor: pointer; color: #fff; font-size: 15px; line-height: 100%; font-family: -apple-system, ".SFNSDisplay-Regular", "Helvetica Neue", Helvetica, Arial, sans-serif; background: #000000; border: 1px solid #000000; filter: "progid:DXImageTransform.Microsoft.gradient(startColorstr=#262626, endColorstr=#000000, GradientType=0)"; background: -webkit-linear-gradient(#262626, #000000); background: linear-gradient(to bottom, #262626, #000000); height: 40px !important; width: 50% !important; }
      `}</style>

      <style jsx global>{`
        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Inter, Segoe UI, Roboto,
            Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
        }

        * {
          box-sizing: border-box;
          margin: 0;
        }

        .amp {
          font-family: Baskerville, Georgia, serif;
          font-style: italic;
        }
      `}</style>

      <style jsx>{`
        .container {
          min-height: 100vh;
          padding: 0 0.5rem;
          display: flex;
          flex-direction: column;
          justify-content: left;
          align-items: left;
        }

        main {
          padding: 0 0 3rem 3rem;
          flex: 1;
          width: 100%;
          max-width: 800px;
        }

        a {
          color: inherit;
          text-decoration: none;
        }

        .twitter {
          width: 32px;
          height: 32px;
          position: relative;
          display: inline-block;
          top: 6px;
          margin-left: 18px;
        }

        .email {
          width: 18px;
          margin-left: 18px;
          position: relative;
          top: -2px;
        }

        .title {
          margin: 0;
          padding: 1rem;
          line-height: 1.15;
          font-size: 2rem;
          color: white;
          background: black;
          display: inline-block;
        }

        label {
          line-height: 1.5;
          font-size: 1.5rem;
          display: block;
          color: #aaa;
          margin-top: 3rem;
        }

        .item {
          font-size: 3rem;
          line-height: 1;
          color: black;
          margin-top: 2rem;
        }

        label + .item {
          margin-top: 0.5rem;
        }

        .gumroad:hover {
          display: inline-block;
          background: url(header_bar.png) bottom center;
        }

        @media (max-width: 600px) {
          main {
            width: 100%;
          }
        }
      `}</style>
    </div>
  )
}
